tinymce.PluginManager.add("download",function(e,t){e.addButton("download",{title:"下载",icon:"code",onclick:function(){e.windowManager.open({title:"专用下载",multiline:!0,minWidth:e.getParam("dialog_width",320),minHeight:e.getParam("dialog_height",Math.min(tinymce.DOM.getViewPort().h-200,200)),body:[{type:"textbox",name:"title",label:"标题"},{type:"textbox",name:"url",label:"地址"}, {type:"textbox",name:"pw",label:"密码"}],onsubmit:function(t){e.insertContent('<div class="reply-read"><div class="reply-ts"><div class="read-sm" style="-webkit-user-select: text;">'+t.data.title+'</div> <div class="read-sm" style="-webkit-user-select: text;">'+t.data.pw +'</div></div><div class=\"read-pl"><a href="'+t.data.url+'" target="_blank" rel="noopener noreferrer">点击下载 </a></div>')}})}}),e.addMenuItem("commenthide",{text:"Example plugin",context:"tools",onclick:function(){e.windowManager.open({width:600,height:400,buttons:[{text:"Insert",onclick:function(){var t=e.windowManager.getWindows()[0];e.insertContent(t.getContentWindow().document.getElementById("content").value),t.close()}},{text:"Close",onclick:"close"}]})}})});




